package week2.day1;

public class ConditionStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -5;
		if (number > 0) {
			System.out.println("The given number is Positive");
		} else if (number < 0) {

			System.out.println("The given number is Negative");

		} else {
			System.out.println("The given number is Negative");
		}
	}

}
